import {
  Label,
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import { useState, useEffect } from "react";

import moment from "moment";

export default function Chart(props) {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [x, setX] = useState(null);
  const [y, setY] = useState(null);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          "http://127.0.0.1:8000/chart/AAPL?rang=ytd"
        );
        const jsonData = await response.json();
        var arrdata = [];
        for (let i = 0; i < jsonData["timestamps"].length; i++) {
          const currenttime = jsonData["timestamps"][i];
          const currentstat = jsonData["stats"][i];
          arrdata.push({ timestamp: currenttime, stat: currentstat });
        }
        setData(arrdata);
        setX(jsonData["timestamps"]);
        setY(jsonData["stats"]);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  console.log(data);
  console.log(x);
  console.log(y);

  //   const CustomTooltip = ({ active, payload, label }) => {
  //     if (active && payload && payload.length) {
  //       return (
  //         <div className="custom-tooltip">
  //           <p className="label">{`${moment(label).format("MM-DD-YY")} : ${
  //             payload[0].value + " lbs"
  //           }`}</p>
  //         </div>
  //       );
  //     }
  //   };

  return (
    <ResponsiveContainer width="100%" height={400}>
      <LineChart width={500} height={300} data={data}>
        <XAxis type="date" dataKey="timestamp" name="date" scale="time" hasTick>
          <Label value="Date" position="bottom" />
        </XAxis>
        <YAxis>
          <Label value="Price" angle={-90} position="insideLeft" />
        </YAxis>

        <CartesianGrid />
        <Line type="monotone" dataKey="stat" unit="lbs" stroke="blue" />
      </LineChart>
    </ResponsiveContainer>
  );
  return <></>;
}
