import {
  Label,
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Legend,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import { useState, useEffect } from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import Chart from "./Chart";

export default function Main(props) {
  const [searchTerm, setSearchTerm] = useState("");
  console.log(props.data);
  const [results, setResults] = useState([]);

  const [data, setData] = useState(null);
  const [tickers, setTickers] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showsearch, setShowsearch] = useState(false);
  const [x, setX] = useState(null);
  const [y, setY] = useState(null);

  const handleSearch = (event) => {
    const { value } = event.target;
    setSearchTerm(value);

    setResults(
      tickers.filter((result) =>
        result.toLowerCase().includes(value.toLowerCase())
      )
    );
  };
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          "http://127.0.0.1:8000/chart/AAPL?rang=ytd"
        );
        const jsonData = await response.json();
        var arrdata = [];
        for (let i = 0; i < jsonData["timestamps"].length; i++) {
          const currenttime = jsonData["timestamps"][i];
          const currentstat = jsonData["stats"][i];
          arrdata.push({ timestamp: currenttime, Price: currentstat });
        }
        setData(arrdata);
        setX(jsonData["timestamps"]);
        setY(jsonData["stats"]);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    const fetchTicks = async () => {
      try {
        const response = await fetch("http://127.0.0.1:8000/tickers");
        const jsonData = await response.json();
        setTickers(jsonData["tickers"]);
        setShowsearch(true);
      } catch (error) {}
    };

    fetchData();
    fetchTicks();
  }, []);

  const fetchStock = async () => {
    try {
      const response = await fetch(
        `http://127.0.0.1:8000/chart/${searchTerm}?rang=ytd`
      );
      const jsonData = await response.json();
      var arrdata = [];
      for (let i = 0; i < jsonData["timestamps"].length; i++) {
        const currenttime = jsonData["timestamps"][i];
        const currentstat = jsonData["stats"][i];
        arrdata.push({ timestamp: currenttime, Price: currentstat });
      }
      setData(arrdata);
      setX(jsonData["timestamps"]);
      setY(jsonData["stats"]);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  return (
    <div id="chart">
      <Chart data={data}></Chart>
      {showsearch && (
        <Container>
          <Row>
            <Col>
              <input
                type="text"
                className="form-control"
                placeholder="Search"
                value={searchTerm}
                onChange={handleSearch}
              />
              {searchTerm && (
                <div
                  className="dropdown-menu show"
                  aria-labelledby="dropdownMenuButton"
                >
                  {results.length > 0 ? (
                    results.map((result, index) => (
                      <a className="dropdown-item" href="#" key={index}>
                        {result}
                      </a>
                    ))
                  ) : (
                    <a className="dropdown-item disabled" href="#">
                      No results found.
                    </a>
                  )}
                </div>
              )}
            </Col>
            <Col>
              <Button onClick={fetchStock}>Submit</Button>
            </Col>
          </Row>
        </Container>
      )}
    </div>
  );
}
