import {
  Label,
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Legend,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import { useState, useEffect } from "react";

import moment from "moment";
import SearchDropdown from "./dropdown";

export default function Chart(props) {
  const [data, setData] = useState(null);
  const [tickers, setTickers] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showsearch, setShowsearch] = useState(false);
  const [x, setX] = useState(null);
  const [y, setY] = useState(null);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          "http://127.0.0.1:8000/chart/AAPL?rang=ytd"
        );
        const jsonData = await response.json();
        var arrdata = [];
        for (let i = 0; i < jsonData["timestamps"].length; i++) {
          const currenttime = jsonData["timestamps"][i];
          const currentstat = jsonData["stats"][i];
          arrdata.push({ timestamp: currenttime, Price: currentstat });
        }
        setData(arrdata);
        setX(jsonData["timestamps"]);
        setY(jsonData["stats"]);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    const fetchTicks = async () => {
      try {
        const response = await fetch("http://127.0.0.1:8000/tickers");
        const jsonData = await response.json();
        setTickers(jsonData["tickers"]);
      } catch (error) {}
    };

    fetchData();
    fetchTicks();
  }, []);

  console.log(data);
  console.log(tickers);

  //   const CustomTooltip = ({ active, payload, label }) => {
  //     if (active && payload && payload.length) {
  //       return (
  //         <div className="custom-tooltip">
  //           <p className="label">{`${moment(label).format("MM-DD-YY")} : ${
  //             payload[0].value + " lbs"
  //           }`}</p>
  //         </div>
  //       );
  //     }
  //   };

  return (
    <div id="chart">
      <ResponsiveContainer width="100%" height={400}>
        <LineChart width={500} height={300} data={data}>
          <XAxis dataKey="timestamp" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="Price" stroke="#8884d8" />
          <CartesianGrid />
        </LineChart>
      </ResponsiveContainer>
      {showsearch && <SearchDropdown data={tickers}></SearchDropdown>}
    </div>
  );
  return <></>;
}
